public class Program
{
    public static void Main()
    {

        //O try serve para tratar um erro e nao parar a execuçao do programa
        //se ocorrer qualquer erro dentro do bloco try o sistema interrombe
        // a execuçao do bloco e vai para o catch
            try
            {
                int numero = int.Parse(Console.ReadLine());
                Console.WriteLine($"Voce digitou o numero {numero}");
                int resultado = 10 / numero;
                Console.WriteLine($"O resultado é {resultado}");
            } 
            //Tratando exceçaode overflow (estouro de campo)
            catch(OverflowException)
            {
                Console.WriteLine("Este numero inteiro é maior que o suportado");
            }
            //Tratando excessao de erro de formato                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
            catch(FormatException)
            {
                Console.WriteLine($"Erro: Digite um numero inteiro");
            }

            //Catch é o tratamento do erro, nomrmamente colocamos as mensagens de acordo como erro
            //com o tipo do erro, para melhor compreensao do usuario
            catch(Exception ex)
            {
                Console.WriteLine($"Ocorreu um erro : {ex.Message}");
            }
            //O finallu é um bloco executado independentemente se ocorrer erro ou nao
            finally
            {
                Console.WriteLine("Entrando no finally");
            }
    }

}
